<template>
  <el-container
    class="main-wrapper"
    style="background-color:white; text-align:left"
  >
    <el-contianer class="main-container">
      <!-- <el-main > -->
      <h1>새로운 글 등록</h1>
      <el-divider></el-divider>
      <el-form :model="form" ref="form">
        <el-form-item
          prop="title"
          label="제목"
          :label-width="formLabelWidth"
          :rules="{
            required: true,
            message: '제목을 입력해주세요.',
            trigger: 'blur'
          }"
        >
          <el-input
            v-model="form.title"
            autocomplete="off"
            placeholder="글 제목을 입력하세요!"
          ></el-input>
        </el-form-item>
        <el-form-item prop="content" label="내용" :label-width="formLabelWidth">
          <el-input
            type="textarea"
            :rows="5"
            placeholder="글 내용을 작성해주세요!"
            v-model="form.content"
          >
          </el-input>
        </el-form-item>
        <el-form-item>
          <el-row>
            <el-col :span="8"
              ><el-upload
                class="upload-demo"
                drag
                ref="upload"
                :action="URL"
                :on-preview="handlePreview"
                :on-remove="handleRemove"
                :file-list="fileList"
                :auto-upload="false"
                multiple
              >
                <div>
                  <img
                    style="width:100px; margin-top:20px;"
                    src="data:image/svg+xml;base64,"
                  />
                </div>
                <!-- <i class="el-icon-upload"></i> -->
                <div class="el-upload__text">
                  파일 드래그 OR <em>클릭하세요!</em>
                </div>
                <el-button
                  style="display:none"
                  size="small"
                  type="success"
                  @click="submitUpload"
                  >upload to server</el-button
                >
                <!-- <template #tip>
                  <div class="el-upload__tip">
                    파일 용량 제한 : 2G
                  </div>
                </template> -->
              </el-upload></el-col
            >
            <el-col :span="16">
              <el-button
                type="info"
                @click="submitCancle()"
                class="create-cancle-btn"
                >취소</el-button
              >
              <el-button
                type="primary"
                class="gaon-button create-btn"
                @click="createPost('form', $route.params.bid)"
                >글 작성</el-button
              ></el-col
            >
          </el-row>
        </el-form-item>
      </el-form>

      <!-- </el-main> -->

      <!-- <template #tip>
            <div class="el-upload__tip">
              jpg/png files with a size less than 500kb
            </div>
          </template>
        </el-upload>

          <el-button type="info" @click="submitCancle();">취소</el-button>
          <el-button type="primary" class="gaon-button" @click="createPost('form', $route.params.bid);">글 작성</el-button>
      </el-main> -->
    </el-contianer>
  </el-container>
</template>

<script>
// API
import $axios from "axios";
export default {
  data() {
    return {
      fileList: [],
      form: {
        title: "",
        uid: "",
        content: ""
      },
      URL: process.env.VUE_APP_API_URL
    };
  },
  methods: {
    submitCancle() {
      // console.log('cancle')
      this.form.title = "";
      this.form.content = "";
      this.$router.push({ name: "board-post-list" });
    },
    submitUpload() {
      this.$refs.upload.submit();
      console.log("files are uploaded");
    },
    // 새 글 생성하기
    async createPost(formname, bid) {
      this.$refs[formname].validate(valid => {
        if (valid) {
          console.log("새 글 생성");
          const url = `/boards/${bid}/posts`;
          const uid = JSON.parse(sessionStorage.getItem("userInfo")).id;
          this.form.uid = uid;
          $axios
            .post(url, this.form)
            .then(res => {
              // console.log(res.data);
              this.form.title = "";
              this.form.content = "";
              this.form.uid = "";
              this.URL += `/api/v1/boards/posts/${res.data.post.id}/files/`;
              // console.log(this.URL)
            })
            .then(res => {
              this.submitUpload();
              this.$router.push({ name: "board-post-list" });
            })
            .catch(err => {
              console.log(err);
            });
        } else {
          console.log("error submit!");
          return false;
        }
      });
    }
  }
};
</script>

