<template lang="">
  <el-container class="main-wrapper" style="
  ">
    <el-contianer class="main-container">
    <!-- <el-scrollbar class="show-scroll" height="400px"> -->
      <el-main style="    text-align: initial;">
        <div class="horizontal-border">
        <p class="title">
        {{ form.title }}
        </p>
          <el-divider></el-divider>
        <p class="content">
        {{ form.nickname }}
        <div class="vertical-border"></div>
        {{ form.created_time.substring(0, 19) }}
        </p>
                <el-divider></el-divider>
        </div>
         <div
        class="pa-5"
        style="min-height: 250px; "
      v-html="enterToBr(form.content)"
      >
      </div>
        <el-button  v-for="file in fileList"
        style="max-width: min-content;     padding-right: 25px;"
          @click="download(file.pfid, file.filename)" >
              <span>{{ file.filename }}</span>
              <img
               style="width:25px;vertical-align:middle"
              src="data:image/svg+xml;base64," />

        </el-button>

        <!-- </p> -->
          <el-footer v-if="form.uid === uid">
        <el-row :gutter="20" v-if="form.uid === uid">
          <el-col :span="6" :offset="18" style="text-align: -webkit-right;">
            <el-button @click="editPost($route.params.pid)">수정</el-button>
            <el-popconfirm
              confirmButtonText='네'
              cancelButtonText='아니요'
              icon="el-icon-info"
              iconColor="red"
              title="정말 글을 삭제하시겠습니까?"
              @confirm="deletePost($route.params.pid)"
            >
            <template #reference>
              <el-button type="danger">삭제</el-button>
              </template>
            </el-popconfirm>

          </el-col>
        </el-row>
      </el-footer>
      </el-main>


      <!-- </el-scrollbar> -->
    </el-contianer>
  </el-container>
</template>
<script>
import { useRoute } from "vue-router";
import $axios from "axios";
export default {
  mounted() {
    // this.$nextTick(function() {
    //       // 모든 화면이 렌더링된 후 실행합니다.
    //   })
    this.uid = JSON.parse(sessionStorage.getItem("userInfo")).id;
    const route = useRoute()
    const url = `/boards/posts/${route.params.pid}`;
    // console.log("mounted")
    $axios
      .get(url)
      .then(res => {
        // console.log(res.data.post)
        this.form = res.data.post
      })
      .catch(err => {
        console.log(err);
      });

    const fileURL = `/boards/posts/${route.params.pid}/files`
    $axios
      .get(fileURL)
      .then(res => {
        console.log(res.data)
        res.data.forEach((element, idx, arr) => this.fileList.push({pfid: arr[idx].pfid, filename: arr[idx].file_name}))
        // this.fileList
        // console.log(this.fileList)
        console.log("get uploaded files")

      })
      .catch(err => {
        console.log(err)
      })


  },
  data() {
    return {
      form: {
          id: 0,
          title: "post_title",
          content: "post_content",
          uid: 0,
          bid: 0,
          created_time: "2021-08-10 00:14:01.130000",
          modified_time: "2021-08-10 00:14:01.130000"
        },
        fileList: [
        ],
        uid: 0
    }
  },
  methods: {
      async getDetail(pid) {
      console.log("get detail post");
      const url = `/boards/posts/${pid}`;
      await $axios
        .get(url)
        .then(res => {
          // console.log(res.data.posts);
          this.form = res.data.post
          // console.log("makeboard ");
          // console.log("res.data");
        })
        .catch(err => {
          console.log(err);
        });
      },

      editPost(pid) {
      this.$router.push({name: 'board-post-modify'})
      },

      async deletePost(pid) {
      // console.log("delete post");
      const url = `/boards/posts/${pid}`;
      await $axios
        .delete(url)
        .then(res => {
          console.log("post is deleted");
          this.$router.push({name: 'board-post-list'})
        })
        .catch(err => {
          console.log(err);
        });
      },

      async download(pfid, filename) {
        console.log(pfid)
        const url = `/boards/posts/files/${pfid}`
        await $axios
          .get(url, {responseType: 'blob'})
          .then(res => {
            console.log(res)
            const fileurl = window.URL.createObjectURL(new Blob([res.data]))
            const link = document.createElement('a')
            link.href = fileurl
            link.setAttribute('download', `${filename}`)
            document.body.appendChild(link)
            link.click()
          })
          .catch(err => {
            console.log(err)
          })
        },
       enterToBr(str) {
      if (str) return str.replace(/(?:\r\n|\r|\n)/g, '<br/>');
    },
    }

};
</script>
